class Solution:
    def lexSmallest(self, s: str) -> str:
        # hmm...
        n = len(s)
        best = s
        for k in range(1,n + 1):
            reversefirst = s[:k][::-1] + s[k:]
            if reversefirst < best:
                best = reversefirst

            reverselast = s[:-k] + s[-k:][::-1]
            if reverselast < best:
                best = reverselast
        return best
