class Solution:
    def hasCycle(self, head: Optional[ListNode]) -> bool:
        if head is None:
            return False
        elif head.next is None:
            return False
        else:
            fastptr = slowptr = head
            while fastptr.next and fastptr.next.next:
                fastptr = fastptr.next.next
                slowptr = slowptr.next
                if slowptr == fastptr:
                    return True
        return False

