class Solution:
    def hasCycle(self, head: Optional[ListNode]) -> bool:
        sets : set = set()
        currentnode = head
        while currentnode :
            if currentnode.next in sets:
                return True
            else :
                sets.add(currentnode)
            currentnode = currentnode.next
        return False
