class Solution:
    def longestBalanced(self, nums: List[int]) -> int:
        # aarggggggggh
        best : int = 0

        for idx in range(len(nums)):
            even_seen : set = set()
            odd_seen : set = set()

            if len(nums) - idx  <= best:
                break

            for jdx in range(idx,len(nums)):
                x = nums[jdx]

                if x % 2 == 0:
                    even_seen.add(x)
                else:
                    odd_seen.add(x)
                if len(even_seen) == len(odd_seen):
                    best = max(best,jdx - idx + 1)

        return best

class Solution:
    def longestBalanced(self, nums: List[int]) -> int:
        # Let me use the two set approach.
        if not nums:
            return 0

        even_set : set = set()
        odd_set : set = set()
        balance : int = 0

        firstseen : dict = {0:-1}
        maxlength : int = 0

        for idx,num in enumerate(nums):
            if num % 2 == 0: # If the number is divisible then...
                if num not in even_set:
                    even_set.add(num)
                    balance += 1
            else:
                if num not in odd_set:
                    odd_set.add(num)
                    balance -= 1

            if balance in firstseen:
                maxlength = max(maxlength,idx - firstseen[balance])
            else:
                firstseen[balance] = idx
        return maxlength
