from collections import defaultdict
class Solution:
    def longestBalanced(self, s: str) -> int:
        # O(N2) solution could work.
        n = len(s)
        maxlength = 0

        for freq in range(1,n + 1):
            count = defaultdict(int)
            left = 0
            distinct = 0

            for right in range(n):
                count[s[right]] += 1
                if count[s[right]] == 1:
                    distinct += 1
                while (right - left + 1) > freq * distinct:
                    count[s[left]] -= 1
                    if count[s[left]] == 0:
                        distinct -= 1
                    left += 1
                if (right - left + 1) == freq * distinct:
                    if all(v == freq for v in count.values() if v > 0):
                        maxlength = max(maxlength,right - left + 1)
        return maxlength
