class Solution:
    def longestBalanced(self, s: str) -> int:
        # Much similar to the last question.But O(N2) is not going to work unfortunately.
        ## 10^5 is a huge string.

        '''
        If the number of a == number of b == number of c then the condition is True.
        So we have to find a way to match this condition and count the length accordingly in either O(nlogn) or O(N2)'''

        # Prefixsum + hashmap could work here.
        a = b = c = 0

        Hashmap : dict = {(0,0):-1}
        maxlength : int = 0

        for idx,char in enumerate(s):
            if char == 'a':
                a += 1
            elif char == 'b':
                b += 1
            else: # The char has to c elsewise.
                c += 1

            # So will get the key and update it in the hashmap.
            key = (a - b,a - c)

            # If the key already exists in the hasmap then...
            if key in Hashmap:
                maxlength = max(maxlength,idx - Hashmap[key])
            else:
                Hashmap[key] = idx

        def ansfunc(x,y,z):
            difference = 0
            first = {0:-1}
            best = 0
            for idx,char in enumerate(s):
                if char == z:
                    difference = 0
                    first = {0:idx}
                else:
                    if char == x:
                        difference += 1
                    else:
                        difference -= 1
                    if difference in first:
                        best = max(best,idx - first[difference])
                    else:
                        first[difference] = idx
            return best

        maxlength = max(maxlength,ansfunc('a','b','c'),ansfunc('a','c','b'),ansfunc('b','c','a'))

        case3 : int = 1
        for idx in range(1,len(s)):
            if s[idx] == s[idx - 1]:
                case3 += 1
            else:
                maxlength = max(maxlength,case3)
                case3 = 1

        maxlength = max(maxlength,case3)

        return maxlength

