class Solution:
    def longestSubarray(self, nums: List[int]) -> int:
        # Slding window approach I think.

        ## So we use a maxanswer.
        maxanswer : int = 2 # Minimum answer is given to be 2.

        current_length = 2

        for idx in range(2,len(nums)):
            if nums[idx] == nums[idx - 1] + nums[idx - 2]: # This is the fibonacci condition.
                current_length += 1
                maxanswer = max(maxanswer,current_length)
            else:
                current_length = 2
        return maxanswer
