class Solution:
    def longestSubsequence(self, nums: List[int]) -> int:
        # Okay so lets compute the Xor sum entirely first.
        if len(nums) <= 0:
            return 0

        xor_sum : int = 0

        for num in nums:
            xor_sum ^= num # Computing the nums.

        if xor_sum != 0:
            return len(nums)

        if any(a != 0 for a in nums):
            return len(nums) - 1

        return 0

