class Solution:
    def lengthOfLongestSubstring(self, s: str) -> int:
        lft_ptr , max_val = 0 , 0 
        seen_char = set()
        for nxt_ptr in range(len(s)) : 
            while s[nxt_ptr] in seen_char:
                seen_char.remove(s[lft_ptr])
                lft_ptr += 1
            seen_char.add(s[nxt_ptr])
            max_val = max(max_val , nxt_ptr - lft_ptr + 1)
        return max_val
