from collections import Counter
class Solution:
    def majorityElement(self, nums: List[int]) -> List[int]:
        # Length of the array is n/3
        n = len(nums)
        Length = int(n / 3)
        Hashmap : dict = Counter(nums)
        result : list = []
        for key in Hashmap:
            if Hashmap[key] > Length:
                result.append(key)
                Hashmap[key] -= 1
        return result

