class Solution:
    def maxSumOfSquares(self, num: int, sum: int) -> str:
        if sum > 9 * num:
            return "" # There is no possibility of finding the maximum score.

        digits = []
        for _ in range(num):
            digit = min(9,sum)
            digits.append(str(digit))
            sum -= digit
        return ''.join(digits)
