class Solution:
    def maxProduct(self, nums: List[int]) -> int:
       # Let me try this with Kadanes Algorithm.
       MaxProduct = nums[0]

       Product = 1

       for num in nums:
            if Product < 0:
                Product = 1

            Product *= num
            MaxProduct = max(Product,MaxProduct)
       return MaxProduct

class Solution:
    def maxProduct(self, nums: List[int]) -> int:
        currentproduct = nums[0]
        maxproduct = nums[0]

        for i in range(1,len(nums)):
            currentproduct = currentproduct * nums[i]
            maxproduct = max(currentproduct,maxproduct)
        return maxproduct
