class Solution:
    def maximumSubarraySum(self, nums: List[int], k: int) -> int:
        # I think we can solve this using Fixed Sliding Window.
        MaxSum : int = 0

        for idx in range(len(nums)):
            window : list = nums[idx:k + idx]
            windowsum : int = 0
            if len(set(window)) == k:
                windowsum = sum(window)
                MaxSum = max(MaxSum,windowsum)
        return MaxSum
