class Solution:
    def mergeAlternately(self, word1: str, word2: str) -> str:
        # Similar approach.
        ans = []
        n1,n2 = len(word1),len(word2)
        for idx in range(max(n1,n2)):
            if idx < n1:
                ans.append(word1[idx])
            if idx < n2:
                ans.append(word2[idx])
        return "".join(ans)

class Solution:
    def mergeAlternately(self, word1: str, word2: str) -> str:
        # Apparently using a list is much more efficient than a string.
        ans = []
        idx = 0
        while idx < len(word1) and idx < len(word2):
            ans.append(word1[idx])
            ans.append(word2[idx])
            idx += 1
        ans.append(word1[idx:])
        ans.append(word2[idx:])
        return "".join(ans)

class Solution:
    def mergeAlternately(self, word1: str, word2: str) -> str:
        # Apparently using a list is much more efficient than a string.
        ans = []
        idx = 0
        while idx < len(word1) and idx < len(word2):
            ans.append(word1[idx])
            ans.append(word2[idx])
            idx += 1
        if idx == len(word1) and idx < len(word2):
            ans.append(word2[idx:])
        elif idx == len(word2) and idx < len(word1):
            ans.append(word1[idx:])
        return "".join(ans)

class Solution:
    def mergeAlternately(self, word1: str, word2: str) -> str:
        # Brute force.
        idx = 0
        ans = ""
        while idx < len(word1) and idx < len(word2):
            ans += word1[idx] + word2[idx]
            idx += 1
        if idx == len(word1) and idx < len(word2):
            ans += word2[idx:]
        elif idx == len(word2) and idx < len(word1):
            ans += word1[idx:]
        return ans
