from typing import List

class Solution:
    def minAbsDiff(self, grid: List[List[int]], k: int) -> List[List[int]]:
        from itertools import chain

        m, n = len(grid), len(grid[0])
        ans = []

        for i in range(m - k + 1):
            row = []
            for j in range(n - k + 1):
                # Extract k x k submatrix starting at (i, j)
                submatrix = [grid[x][j:j + k] for x in range(i, i + k)]
                elements = sorted(set(chain.from_iterable(submatrix)))

                # If there's only one unique value, min difference is 0
                if len(elements) <= 1:
                    row.append(0)
                else:
                    # Calculate minimum absolute difference between consecutive elements
                    min_diff = float('inf')
                    for x in range(1, len(elements)):
                        min_diff = min(min_diff, elements[x] - elements[x - 1])
                    row.append(min_diff)
            ans.append(row)

        return ans
