class Solution:
    def fillCups(self, amount: List[int]) -> int:
        minimumseconds = 0
        idx = 0
        while sum(amount) > 0:
            if amount[idx % 3] and amount[(idx + 1) % 3]:
                amount[idx % 3] -= 1
                amount[(idx + 1) % 3] -= 1
                minimumseconds += 1
                idx += 1
            elif amount[idx % 3] and amount[(idx + 2) % 3]:
                amount[idx % 3] -= 1
                amount[(idx + 2) % 3] -= 1
                minimumseconds += 1
                idx += 1
            else:
                amount[idx % 3] -= 1
                minimumseconds += 1
        return minimumseconds

class Solution:
    def fillCups(self, amount: List[int]) -> int:
        coldwater = amount[0]
        warmwater = amount[1]
        hotwater = amount[2]
        count = 0
        while coldwater or warmwater or hotwater:
            if (coldwater and warmwater) or (warmwater and hotwater) or (hotwater and coldwater): 
                if coldwater and warmwater:
                    coldwater -= 1
                    warmwater -= 1
                    count += 1
                if warmwater and hotwater:
                    warmwater -= 1
                    hotwater -= 1
                    count += 1
                if coldwater and hotwater:
                    coldwater -= 1
                    hotwater -= 1
                    count += 1
            else:
                if coldwater:
                    coldwater -= 1
                    count += 1
                elif hotwater:
                    hotwater -= 1
                    count += 1
                elif warmwater:
                    warmwater -= 1
                    count += 1
        return count
