from typing import List
from collections import deque, defaultdict

class Solution:
    def minMoves(self, classroom: List[str], energy: int) -> int:
        m, n = len(classroom), len(classroom[0])
        grid = [list(row) for row in classroom]

        litter_indices = {}
        start = None
        idx = 0
        for i in range(m):
            for j in range(n):
                if grid[i][j] == 'S':
                    start = (i, j)
                elif grid[i][j] == 'L':
                    litter_indices[(i, j)] = idx
                    idx += 1

        total_litters = len(litter_indices)
        target_mask = (1 << total_litters) - 1

        # State: (x, y, mask)
        best_energy = dict()
        q = deque()
        q.append((start[0], start[1], 0, energy))
        best_energy[(start[0], start[1], 0)] = energy

        dirs = [(-1,0), (1,0), (0,-1), (0,1)]
        steps = 0

        while q:
            for _ in range(len(q)):
                x, y, mask, e = q.popleft()

                if mask == target_mask:
                    return steps

                for dx, dy in dirs:
                    nx, ny = x + dx, y + dy

                    if not (0 <= nx < m and 0 <= ny < n):
                        continue
                    if grid[nx][ny] == 'X':
                        continue

                    ne = e - 1
                    if ne < 0:
                        continue

                    if grid[nx][ny] == 'R':
                        ne = energy

                    nmask = mask
                    if grid[nx][ny] == 'L':
                        nmask |= (1 << litter_indices[(nx, ny)])

                    key = (nx, ny, nmask)

                    # Only continue if we reach this state with more energy
                    if key not in best_energy or best_energy[key] < ne:
                        best_energy[key] = ne
                        q.append((nx, ny, nmask, ne))
            steps += 1

        return -1
