class Solution:
    def minOperations(self, boxes: str) -> List[int]:
        # Lemme try solving this.
        # Brute forcing it first.
        ans = []
        for idx in range(len(boxes)):
            c = 0
            for jdx in range(len(boxes)):
                if idx == jdx:
                    continue
                if boxes[jdx] == '1':
                    c += abs(jdx - idx)
            ans.append(c)
        return ans
