class Solution:
    def minSubArrayLen(self, target: int, nums: List[int]) -> int:
        left = 0
        mlength = float('inf')
        sum = 0

        for right in range(len(nums)):
            sum += nums[right]
            while sum >= target:
                mlength = min(mlength,right - left + 1)
                sum = sum - nums[left]
                left += 1
        if (mlength != float('inf')):
            return mlength
        else : return 0
