class Solution:
    def minLengthAfterRemovals(self, s: str) -> int:
        return len(s) - 2*min(s.count('a'),s.count('b'))

class Solution:
    def minLengthAfterRemovals(self, s: str) -> int:
        n = len(s)
        count_a = s.count('a')
        count_b = s.count('b')
        if count_a > count_b:
            return n - count_b*2
        else:
            return n - count_a*2
