class Solution:
    def minimumTime(self, d: List[int], r: List[int]) -> int:
        time = 0
        remaining = d[:]

        while remaining[0] > 0 or remaining[1] > 0:
            time += 1
            can_deliver = [time % r[0] != 0 and remaining[0] > 0,
                           time % r[1] != 0 and remaining[1] > 0]
            if can_deliver[0] and(not can_deliver[1] or remaining[0] >= remaining[1]):
                remaining[0] -= 1
            elif can_deliver[1]:
                remaining[1] -= 1
            # Otherwise they are recharging.
        return time
