class Solution:
    def minCost(self, colors: str, neededTime: List[int]) -> int:
        # Solving this one last time.
        prev = neededTime[0]
        high = 0
        for idx in range(1,len(colors)):
            if colors[idx] == colors[idx - 1]:
                high += min(prev,neededTime[idx])

                prev = max(prev,neededTime[idx])
            else:
                prev = neededTime[idx]
        return high

class Solution:
    def minCost(self, colors: str, neededTime: List[int]) -> int:
        if not colors or not neededTime:
            return 0
        prev_high = neededTime[0]
        maxcolors = 0
        for idx in range(1,len(colors)):
            if colors[idx] == colors[idx - 1]:
                maxcolors += min(neededTime[idx],prev_high)

                prev_high = max(neededTime[idx],prev_high)
            else:
                prev_high = neededTime[idx]
        return maxcolors

class Solution:
    def minCost(self, colors: str, neededTime: List[int]) -> int:
        if not colors or not neededTime:
            return 0
        maxcolors : int = 0
        for idx in range(len(colors) - 1):
            if colors[idx] == colors[idx + 1]:
                if neededTime[idx] > neededTime[idx + 1]:
                    maxcolors += neededTime[idx + 1]
                else:
                    maxcolors += neededTime[idx]
        return maxcolors
