    def moveZeroes(self, nums: List[int]) -> None:
        """
        Do not return anything, modify nums in-place instead.
        """
        if 0 not in nums:
            return nums

        left_ptr , right_ptr = 0 , 1
        while right_ptr < len(nums):
            if nums[left_ptr] == 0 and nums[right_ptr] != 0:
                nums[left_ptr] , nums[right_ptr] = nums[right_ptr],nums[left_ptr]
                left_ptr += 1
                right_ptr += 1
            elif nums[right_ptr] == 0 or left_ptr == right_ptr:
                right_ptr += 1
            elif left_ptr < right_ptr and nums[left_ptr] != 0:
                left_ptr += 1
        return nums

