from itertools import permutations

class Solution:
    balanced_numbers = None

    @staticmethod
    def init_balanced_numbers():
        s = set()
        bases = [
            "1", "22", "122", "333", "1333", "4444", "14444", "22333", "55555",
            "122333", "155555", "224444", "666666", "1224444", "1666666",
            "2255555", "3334444", "7777777", "12255555"
        ]

        for base in bases:
            for p in set(permutations(sorted(base))):
                s.add(int("".join(p)))
        return sorted(s)

    def __init__(self):
        if Solution.balanced_numbers is None:
            Solution.balanced_numbers = Solution.init_balanced_numbers()

    def nextBeautifulNumber(self, n: int) -> int:
        for x in Solution.balanced_numbers:
            if x > n:
                return x
        return -1

class Solution:
    def nextBeautifulNumber(self, n: int) -> int:
        nums = []
        self.generate(0, [0] * 10, nums)
        nums.sort()
        for num in nums:
            if num > n:
                return num
        return -1

    def generate(self, num: int, count: list[int], nums: list[int]) -> None:
        if num > 0 and self.is_beautiful(count):
            nums.append(num)
        if num > 1224444:
            return

        for d in range(1, 8):
            if count[d] < d:
                count[d] += 1
                self.generate(num * 10 + d, count, nums)
                count[d] -= 1

    def is_beautiful(self, count: list[int]) -> bool:
        for d in range(1, 8):
            if count[d] != 0 and count[d] != d:
                return False
        return True
