class Solution:
    def numberOfBeams(self, bank: List[str]) -> int:
        # Daily problem for 27-10-2025.

        # So basically if there is a row , the next row has to be 0 integer wise so that we can get the lazer count.

        overall_lazer_count = 0
        checker = False
        for security_row in range(len(bank) - 1):
            if security_row == len(bank) - 1:
                return overall_lazer_count
            if int(bank[security_row]) != 0 and int(bank[security_row + 1]) == 0 and not checker:
                bank.pop(security_row + 1)
                if security_row == len(bank) - 1:
                    return overall_lazer_count
                overall_lazer_count += bank[security_row].count('1') * bank[security_row + 1].count('1')
                checker = True
            elif  checker and int(bank[security_row]) != 0:
                overall_lazer_count += bank[security_row].count('1') * bank[security_row + 1].count('1')
        return overall_lazer_count
