class Solution:
    def numberOfSubstrings(self, s: str) -> int:
        count : int = 0
        left : int = 0
        char_count : dict = {'a':0,'b':0,'c':0}

        for right in range(len(s)):
            char_count[s[right]] += 1

            while char_count['a'] > 0 and char_count['b'] > 0 and char_count['c'] > 0:
                count += len(s) - right
                char_count[s[left]] -= 1
                left += 1
        return count

class Solution:
    def numberOfSubstrings(self, s: str) -> int:
        if len(s) <= 0:
            return 0

        count: int = 0
        required: set = {'a', 'b', 'c'}
        l = 0
        r = 0

        while l < len(s):
            window_chars = set()
            r = l
            while r < len(s):
                window_chars.add(s[r])
                if required.issubset(window_chars):
                    count += len(s) - r
                    break
                r += 1
            l += 1

        return count

