class Solution:
    def checkInclusion(self, s1: str, s2: str) -> bool:
        if len(s1) > len(s2) : return False
        Ideal_freq = [0]*26
        Window_freq = [0]*26
        for i in range(len(s1)):
            Ideal_freq[ord(s1[i]) - ord('a')] += 1
            Window_freq[ord(s2[i]) - ord('a')] += 1
        if Ideal_freq == Window_freq :
            return True
        for nxt_ptr in range(len(s1),len(s2)):
            Window_freq[ord(s2[nxt_ptr]) - ord('a')] += 1
            Window_freq[ord(s2[nxt_ptr - len(s1)]) - ord('a')] -= 1
            if Window_freq == Ideal_freq:
                return True
        return False

