from collections import Counter
class Solution:
    def checkInclusion(self, s1: str, s2: str) -> bool:
        Ideal_freq_count : dict = Counter(s1)
        Window_freq_count : dict = Counter(s2[:len(s1)])
        lft_ptr : int = 0
        if Window_freq_count == Ideal_freq_count:
                return True
        for nxt_ptr in range(len(s1),len(s2)) : 
            if Ideal_freq_count == Window_freq_count:
                return True
            Window_freq_count[s2[nxt_ptr]] += 1
            Window_freq_count[s2[lft_ptr]] -= 1
            if Window_freq_count[s2[lft_ptr]] == 0:
                del Window_freq_count[s2[lft_ptr]]
            lft_ptr += 1
            if Window_freq_count == Ideal_freq_count:
                return True
        return False

