class Solution:
    def removeOuterParentheses(self, s: str) -> str:
        # Instead of using a flag , we use a concept called calculating the depth apparently.
        depth = 0
        stack = []
        for ch in s:
            if ch == '(':
                if depth > 0:
                    stack.append(ch)
                depth += 1
            else:
                depth -= 1
                if depth > 0:
                    stack.append(ch)
        return "".join(stack)
