from collections import defaultdict
class Solution:
    def judgeCircle(self, moves: str) -> bool:
        Movement = defaultdict(int)
        for move in moves:
            if move == 'D':
                if 'U' in Movement:
                    Movement['U'] -= 1
                    if Movement['U'] == 0:
                        del Movement['U']
                else:
                    Movement['D'] += 1
            elif move == 'U':
                if 'D' in Movement:
                    Movement['D'] -= 1
                    if Movement['D'] == 0:
                        del Movement['D']
                else:
                    Movement['U'] += 1
            elif move == 'L':
                if 'R' in Movement:
                    Movement['R'] -= 1
                    if Movement['R'] == 0:
                        del Movement['R']
                else:
                    Movement['L'] += 1
            elif move == 'R':
                if 'L' in Movement:
                    Movement['L'] -= 1
                    if Movement['L'] == 0:
                        del Movement['L']
                else:
                    Movement['R'] += 1
        return not Movement
