# Definition for a binary tree node.
# class TreeNode:
#     def __init__(self, val=0, left=None, right=None):
#         self.val = val
#         self.left = left
#         self.right = right
class Solution:
    def isSameTree(self, p: Optional[TreeNode], q: Optional[TreeNode]) -> bool:
        # This is solvable.
        ## We can create a function that returns the order in which the nodes are executed using preorder-traversal.
        def preorder(root) -> list:
            if not root:
                return []
            return [root.val] + [preorder(root.left)] + [preorder(root.right)]
        return preorder(p) == preorder(q)
