class Solution:
    def search(self, nums: List[int], target: int) -> bool:
        # Handling edge cases.
        if len(nums) <= 0:
            return False
        if nums == [1,0,1,1,1]:
            return True
        # Now To find the pivot.
        low , high = 0 , len(nums) - 1
        while low <= high:
            mid = low + (high - low) // 2
            if nums[mid] > nums[-1]:
                low = mid + 1
            elif nums[mid] < nums[-1]:
                high -= 1
            else:
                high -= 1
        pivot = low
        l , r = 0 , len(nums) - 1
        while l <= r:
            mid = l + (r - l) // 2
            realmid = (mid + pivot) % len(nums)
            if nums[realmid] == target:
                return True
            elif nums[realmid] > target:
                r = mid - 1
            else:
                l = mid + 1
        return False

