class Solution:
    def findErrorNums(self, nums: List[int]) -> List[int]:
        expectedsum = sum(x for x in range(1,len(nums) + 1))
        actualsum = sum(nums)
        missingsum = sum(set(nums))
        return [actualsum - missingsum,expectedsum - missingsum]

class Solution:
    def findErrorNums(self, nums: List[int]) -> List[int]:
        # Lets find the missing number and duplicate as well.
        if not nums:
            return []
        duplicate = -1
        seen = set(nums)
        for num in nums:
            if num in seen:
                seen.remove(num)
                continue
            else:
                duplicate = num
                break
        missing = -1
        for num in range(1,len(nums) + 1):
            if num not in set(nums):
                missing = num
                break
        return [duplicate,missing]

class Solution:
    def findErrorNums(self, nums: List[int]) -> List[int]:
        seen : set = set(nums)
        ans : list = []
        for idx in range(len(nums)):
            if nums[idx] in seen:
                seen.remove(nums[idx])
                continue
            ans.append(nums[idx])
            ans.append(len(nums) - idx + 1)
        return ans
