    def sortColors(self, nums: List[int]) -> None:
        """
        Do not return anything, modify nums in-place instead.
        """
        lo = mid = 0
        hi = len(nums) - 1

        while mid <= hi:
            if nums[mid] == 0:
                nums[lo] , nums[mid] = nums[mid] , nums[lo]
                lo += 1
                mid += 1
            elif nums[mid] == 1:
                mid += 1
            else:
                nums[mid] , nums[hi] = nums[hi] , nums[mid]
                hi -= 1

from collections import Counter
class Solution:
    def sortColors(self, nums: List[int]) -> None:
        """
        Do not return anything, modify nums in-place instead.
        """
        counter_dict = Counter(nums)
        n = len(nums)
        nums.clear()
        for i in range(n):
            if counter_dict[0]:
                nums.append(0)
                counter_dict[0] -= 1
            elif counter_dict[1]:
                nums.append(1)
                counter_dict[1] -= 1
            elif counter_dict[2]:
                nums.append(2)
                counter_dict[2] -=1
