class Solution:
    def sortedSquares(self, nums: List[int]) -> List[int]:
        l,r = 0,len(nums) - 1

        while(l < r):
            if nums[l]**2 > nums[r]**2:
                nums[l],nums[r] = nums[r],nums[l]
                r -= 1
            else:
                r -= 1
        return [num**2 for num in nums]
