        return False

class Solution:
    def isStrictlyPalindromic(self, n: int) -> bool:
        def to_base(num,base):
            if num == 0:
                return "0"

            result = ""

            while num > 0:
                num,remainder = divmod(num,base)
                result = str(remainder) + result

            return result

        for i in range(2,n - 1):
            num = n
            op = to_base(num,i)
            if not op[::] == op[::-1]:
                return False
        return True
