class Solution:
    def myAtoi(self, s: str) -> int:
        s = s.lstrip()  # safer than strip() for leading whitespace only
        if not s:
            return 0

        number = ""
        sign_seen = False

        for idx, char in enumerate(s):
            if char in "+-" and not sign_seen and idx == 0:
                number += char
                sign_seen = True
            elif char.isdigit():
                number += char
            else:
                break

        if number in ("", "+", "-"):
            return 0

        num = int(number)
        INT_MIN, INT_MAX = -2**31, 2**31 - 1
        return max(INT_MIN, min(INT_MAX, num))

class Solution:
    def myAtoi(self, s: str) -> int:
        # Edge cases.
        if s == "" or s.strip()[0].isalpha() or s.strip()[0] == ".":
            return 0

        s = s.strip() # Removing the whitespaces.

        if s.isdigit():
            if int(s) < -2**31:
                return -2**31
            elif int(s) > 2**31 - 1:
                return 2**31 - 1
            return int(s) # If this is a number.

        number = ""
        for idx,char in enumerate(s):
            if char == "-" and idx == 0:
                number += char
            elif char == "-" and idx > 0:
                break
            elif char.isdigit() == False:
                break
            else:
                number += char
        if number == "-":
            return 0
        else:
            if int(number) < -2**31:
                return -2**31
            elif int(number) > 2**31 - 1:
                return 2**31 - 1
            return int(number) # If this is a number.

