class Solution:
    def subarraySum(self, nums: List[int], k: int) -> int:
      Prefixsum = 0
      count = 0

      FrequencyHashmap = {0:1} # This is extremely important and moreover the Prefixsum is at 0 currently.

      for num in nums:
        Prefixsum += num

        if Prefixsum - k in FrequencyHashmap:
            count += FrequencyHashmap[Prefixsum - k]


        if Prefixsum in FrequencyHashmap:
            FrequencyHashmap[Prefixsum] += 1
        else:
            FrequencyHashmap[Prefixsum] = 1
      return count

class Solution:
    def subarraySum(self, nums: List[int], k: int) -> int:
        # First letme try solving using bruteforce....
        Prefixsum = [0]*len(nums)
        Prefixsum[0] = nums[0]
        for i in range(1,len(nums)):
            Prefixsum[i] = nums[i] + Prefixsum[i - 1]
        count = Prefixsum.count(k)
        for i in range(len(Prefixsum) - 1):
            for j in range(i+1,len(Prefixsum)):
                if Prefixsum[j] - Prefixsum[i] == k:
                    count += 1
        return count
