class Solution:
    def sumOfUnique(self, nums: List[int]) -> int:
     # Lets make it more memory efficient.
     freq : dict = {}
     for num in nums:
        freq[num] = freq.get(num,0) + 1
     return sum(num for num in nums if freq[num] == 1)

from collections import Counter
class Solution:
    def sumOfUnique(self, nums: List[int]) -> int:
        freq_count : dict = Counter(nums)
        sum : int = 0
        for i in range(len(nums)):
            if freq_count[nums[i]] == 1:
                sum += nums[i]
        return sum
