class Solution:
    def twoSum(self, numbers: List[int], target: int) -> List[int]:
        if len(numbers) == 0:
            return []

        L , R = 0 , len(numbers) - 1

        while L < R:
            value  = numbers[L] + numbers[R]
            if value == target:
                return [L + 1,R + 1]
            elif value > target:
                R -= 1
            else:
                L += 1
        return []

class Solution:
    def twoSum(self, numbers: List[int], target: int) -> List[int]:
        # Two pointer approach.
        lft_ptr , rght_ptr = 0 , len(numbers) - 1
        while(lft_ptr < rght_ptr) : 
            sum : int = numbers[lft_ptr] + numbers[rght_ptr]
            if sum == target:
                return [lft_ptr + 1 , rght_ptr + 1]
                break
            elif sum > target : rght_ptr -= 1
            else : lft_ptr += 1
        return False
