class Solution:
    def isPalindrome(self, s: str) -> bool:
        # List comprehension method.
        Filtered_List = [char.lower() for char in s if char.isalnum()]
        if Filtered_List[:] == Filtered_List[::-1]:
            return True
        else : return False

class Solution:
    def isPalindrome(self, s: str) -> bool:
        left_ptr , right_ptr = 0 , len(s) - 1

        while left_ptr < right_ptr : 
            if s[left_ptr].isalnum() and s[right_ptr].isalnum():
                if s[left_ptr].lower() != s[right_ptr].lower():
                    return False
                left_ptr += 1
                right_ptr -= 1
            elif not s[left_ptr].isalnum() : left_ptr += 1
            else : right_ptr -= 1
        return True

