class Solution:
    def isValid(self, s: str) -> bool:
        ans : list = []
        for i in range(len(s) - 1,-1,-1):
            element = s[i]
            if (element == "]" or element == "}" or element == ")"):
                ans.append(element)
            elif (element == "[" or element == "{" or element == "("):
                if (element == "[" and "]" in ans):
                    ans.remove("]")
                elif (element == "{" and "}" in ans):
                    ans.remove("}")
                elif (element == "(" and ")" in ans):
                    ans.remove(")")
                else:
                    return False
        if len(ans) == 0:
            return True
        else:
            return False
