class Solution:
    def validateStackSequences(self, pushed: List[int], popped: List[int]) -> bool:
        # This problem seems to be a bit more easier than expected.
        stack = []
        left = 0
        for idx in range(len(pushed) + 1):
            while stack and stack[-1] == popped[left]:
                stack.pop()
                left += 1
            if idx < len(pushed):
                stack.append(pushed[idx])
        return len(stack) == 0
