class Solution:
    def isZeroArray(self, nums: List[int], queries: List[List[int]]) -> bool:
        # For each positive element in nums, check if there's a query that can decrement it
        for i, num in enumerate(nums):
            # Skip elements that are already 0
            if num == 0:
                continue

            # Check if element can be decremented to 0
            decrements_needed = num
            for start, end in queries:
                if start <= i <= end:  # Element is in query range
                    decrements_needed -= 1
                    if decrements_needed == 0:
                        break

            # If we can't decrement this element to 0, return False
            if decrements_needed > 0:
                return False

        return True

class Solution:
    def isZeroArray(self, nums: List[int], queries: List[List[int]]) -> bool:
        n, m=len(nums), len(queries)
        freq=[0]*(n+1)
        for s, e in queries:
            freq[s]+=1
            freq[e+1]-=1
        op=0
        for i, x in enumerate(nums):
            op+=freq[i]
            if x>op: return False
        return True
